const express = require('express');

const router = express.Router();
const userController = require('../controllers/userLogic');

//upload-data
router.post('/upload-data', userController.postUserUpload);

//Users which have income lower than $5 USD and have a car of brand “BMW” or “Mercedes”.
router.get('/get-car-income-data', userController.getUserWithCar);

// Male Users which have phone price greater than 10,000.
router.get('/get-male-user-price', userController.getMaleUserPrice);

// Users whose last name starts with “M” and has a quote character length greater than 15 and email includes his/her last name.

router.get('/get-name-starts', userController.getMQ);

//4. Users which have a car of brand “BMW”, “Mercedes” or “Audi” and whose email does not include any digit.

router.get('/get-car-email', userController.getCarAndEmail);

//5. Show the data of top 10 cities which have the highest number of users and their average income.

router.get('/get-user-city', userController.getCities);

module.exports = router;
